import React, { Component } from "react";
import { fetchNewItinerary } from "../store/actions/itineraryActions";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
class AddItinerary extends Component {
  constructor() {
    super();
    this.state = {
      name: null,
      profile: null,
      activities: [],
      hashtags: [],
      price: null,
      duration: null,
      rating: null,
    };
  }

  handelChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value,
    });
  };
  handelSubmit = (e) => {
    e.preventDefault();
    if (!this.props.user.isLoggedin) {
      alert(" you need to login");
      return <Redirect to="/Login" />;
    } else if (
      this.state.name === "" ||
      this.state.profile === "" ||
      this.state.activities === ""
    ) {
      alert("You need to fill these fields");
    } else {
      const newitinerary = {
        name: this.state.name,
        profile: this.state.profile,
        rating: this.state.rating,
        hashtags: this.state.hashtags,
        activities: this.state.activities,
        duration: this.state.duration,
        price: this.state.price,
      };
      // retrieve token from local storage and send it to the add city action
      //where to write condition for user login?
      var token = localStorage.getItem("token");
      this.props.fetchNewItinerary(newitinerary, token);
      console.log("itinerary", newitinerary, token);
    }
  };
  render() {
    return (
      <div>
        <Form onSubmit={this.handelSubmit}>
          <Form.Group controlId="formBasicName">
            <Form.Label>Name</Form.Label>
            <Form.Control type="name" placeholder="Enter city name" />
          </Form.Group>

          <Form.Group controlId="formBasicProfile">
            <Form.Label>Profile</Form.Label>
            <Form.Control
              type="url"
              placeholder="Profile"
              onChange={this.handelChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicDuration">
            <Form.Label>duration</Form.Label>
            <Form.Control
              type="text"
              placeholder="duration"
              onChange={this.handelChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicRating">
            <Form.Label>rating</Form.Label>
            <Form.Control
              type="number"
              placeholder="rating"
              onChange={this.handelChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicActivities">
            <Form.Label>activities</Form.Label>
            <Form.Control
              type="text"
              placeholder="activities"
              onChange={this.handelChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicHashtags">
            <Form.Label>Hashtags</Form.Label>
            <Form.Control
              type="text"
              placeholder="Hashtags"
              onChange={this.handelChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPrice">
            <Form.Label>price</Form.Label>
            <Form.Control
              type="text"
              placeholder="price"
              onChange={this.handelChange}
            />
          </Form.Group>

          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
        {/* //   className="card p-2 card-itinerary-add border-info"
      //   style={{ width: "18rem" }}
