import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchProfileFavorites } from "../store/actions/itineraryActions";
import { login } from "../store/actions/usersAction";
import Itinerary from "./Itinerary";
// import { fetchUsersAction } from "../store/actions/usersAction";
class UserAccount extends Component {
  // profileFavorites = () => {
  //   const ids = this.props.users.favorites;
  //   console.log("ids", ids);
  //   this.props.fetchProfileFavorites(ids);
  // };
  componentDidMount() {
    const ids = this.props.users.favorites;
    console.log("ids", ids);
    const itineraries = this.props.itineraries.includes(ids);
    this.props.fetchProfileFavorites(ids);
    const city = this.props.match.params.city;

    this.props.fetchItinerariesByCityName(itineraries);
  }

  render() {
    const ids = this.props.users.favorites;
    const itineraries = this.props.itineraries;
    console.log("from userAccount", this.props.users);
    return (
      <div className="container">
        {this.props.users.favorites &&
        this.props.users.favorites === itineraries._id ? (
          <React.Fragment>
            <img src={itineraries.id === ids} alt="" />

            <Itinerary itineraries={this.itineraries} />
          </React.Fragment>
        ) : (
          <h4>You Have No Liked Itinerary</h4>
        )}
      </div>
    );
  }
}
const mapStateToProps = (state, ownProps) => {
  return {
    users: state.users,
    itineraries: state.itineraries.itineraries,
  };
};
const mapDispatchToProps = (dispatch) => ({
  login: (userData) => dispatch(login(userData)),
  fetchProfileFavorites: (ids, itineraries) =>
    dispatch(fetchProfileFavorites(ids, itineraries)),
});

export default connect(mapStateToProps, mapDispatchToProps)(UserAccount);
