import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchProfileFavorites } from "../store/actions/itineraryActions";
import { login } from "../store/actions/usersAction";
import Itinerary from "./Itinerary";
// import { fetchUsersAction } from "../store/actions/usersAction";
class UserAccount extends Component {
  // profileFavorites = () => {
  //   const ids = this.props.users.favorites;
  //   console.log("ids", ids);
  //   this.props.fetchProfileFavorites(ids);
  // };
  componentDidMount() {
    const ids = this.props.users.favorites;
    console.log("ids", ids);
    const itineraries = this.props.itineraries.filter((itinerary) => {
      const ids = this.props.users.favorites;

      let itineraryFavorite = itinerary.id === ids;
      return itineraryFavorite;
    });
    this.props.fetchProfileFavorites(ids, itineraryFavorite);
  }

  render() {
    const ids = this.props.users.favorites;

    console.log("from userAccount", this.props.users);
    return (
      <div className="container">
        {this.props.users.favorites && this.props.itineraries}
      </div>
    );
  }
}
const mapStateToProps = (state, ownProps) => {
  return {
    users: state.users,
    itineraries: state.itineraries.itineraries,
  };
};
const mapDispatchToProps = (dispatch) => ({
  login: (userData) => dispatch(login(userData)),
  fetchProfileFavorites: (ids, itineraryFavorite) =>
    dispatch(fetchProfileFavorites(ids, itineraryFavorite)),
});

export default connect(mapStateToProps, mapDispatchToProps)(UserAccount);
