import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchProfileFavorites } from "../store/actions/itineraryActions";
import { login } from "../store/actions/usersAction";
// import { fetchUsersAction } from "../store/actions/usersAction";
class UserAccount extends Component {
  // profileFavorites = () => {
  //   const ids = this.props.users.favorites;
  //   console.log("ids", ids);
  //   this.props.fetchProfileFavorites(ids);
  // };
  componentDidMount() {
    const ids = this.props.users.favorites;
    const email = this.props.users.email;
    console.log("ids", ids);
    const favoritesItineraries = this.props.itineraries.includes(email);

    this.props.fetchProfileFavorites(ids, favoritesItineraries);
  }

  render() {
    const itineraries = this.favoritesItineraries;

    console.log("from userAccount", this.props.users);
    return (
      <div className="container">
        {this.props.users.email === itineraries ? (
          <img src={itineraries.prfile} alt={itineraries.name} />
        ) : (
          <h4>You Have No Liked Itinerary</h4>
        )}
      </div>
    );
  }
}
const mapStateToProps = (state, ownProps) => {
  return {
    users: state.users,
    itineraries: state.itineraries.itineraries,
  };
};
const mapDispatchToProps = (dispatch) => ({
  login: (userData) => dispatch(login(userData)),
  fetchProfileFavorites: (ids, favoritesItineraries) =>
    dispatch(fetchProfileFavorites(ids, favoritesItineraries)),
});

export default connect(mapStateToProps, mapDispatchToProps)(UserAccount);
