import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchProfileFavorites } from "../store/actions/itineraryActions";
import { login } from "../store/actions/usersAction";
import Itinerary from "./Itinerary";
// import { fetchUsersAction } from "../store/actions/usersAction";
class UserAccount extends Component {
  // profileFavorites = () => {
  //   const ids = this.props.users.favorites;
  //   console.log("ids", ids);
  //   this.props.fetchProfileFavorites(ids);
  // };
  componentDidMount() {
    const ids = this.props.users.favorites;
    console.log("ids", ids);
    const name = this.props.itineraries.name;
    this.props.fetchProfileFavorites(ids);
  }
  filter() {
    if (this.props.users.favorites) {
      const filterItinerary = this.props.itineraries.filter(
        (itinerary, index) => {
          let ids = this.props.users.favorites;
          let favoriteItinerary = Itinerary.id.includes(ids);
          return favoriteItinerary;
        }
      );
      return filterItinerary;
    } else return [];
  }
  render() {
    const filterFavorites = this.filter;

    console.log("from userAccount", this.props.users);
    return (
      <div className="container">
        {filterFavorites &&
          filterFavorites.map((itinerary, index) => {
            return <img src={itinerary.prfile} />;
          })}
      </div>
    );
  }
}
const mapStateToProps = (state, ownProps) => {
  return {
    users: state.users,
    itineraries: state.itineraries.itineraries,
  };
};
const mapDispatchToProps = (dispatch) => ({
  login: (userData) => dispatch(login(userData)),
  fetchProfileFavorites: (ids) => dispatch(fetchProfileFavorites(ids)),
});

export default connect(mapStateToProps, mapDispatchToProps)(UserAccount);
